// SPDX-License-Identifier: MIT
pragma solidity >=0.8.14;

// solhint-disable not-rely-on-time

import {Arrays} from "../../libs/Arrays.sol";
import {MinterEvent, InterestRateEvent} from "../../libs/Events.sol";
import {Error} from "../../libs/Errors.sol";
import {WadRay} from "../../libs/WadRay.sol";
import {FixedPoint} from "../../libs/FixedPoint.sol";
import {IERC20Upgradeable} from "../../shared/IERC20Upgradeable.sol";
import {SafeERC20Upgradeable} from "../../shared/SafeERC20Upgradeable.sol";
import {IKreskoAssetIssuer} from "../../kreskoasset/IKreskoAssetIssuer.sol";

import {LibDecimals} from "../libs/LibDecimals.sol";
import {LibCalculation} from "./LibCalculation.sol";
import {KrAsset} from "../MinterTypes.sol";
import {irs} from "../InterestRateState.sol";
import {MinterState} from "../MinterState.sol";

library LibRepay {
    using Arrays for address[];

    using LibDecimals for uint8;
    using LibDecimals for uint256;
    using WadRay for uint256;

    using FixedPoint for FixedPoint.Unsigned;
    using SafeERC20Upgradeable for IERC20Upgradeable;
    using LibCalculation for MinterState;

    /// @notice Repay user kresko asset debt with stability rate updates.
    /// @dev Updates the principal in MinterState and stability rate adjusted values in InterestRateState
    /// @param _kreskoAsset the asset being repaid
    /// @param _anchor the anchor token of the asset being repaid
    /// @param _burnAmount the asset amount being burned
    /// @param _account the account the debt is subtracted from
    function repay(
        MinterState storage self,
        address _kreskoAsset,
        address _anchor,
        uint256 _burnAmount,
        address _account
    ) internal {
        // Update global debt index for the asset
        uint256 newDebtIndex = irs().srAssets[_kreskoAsset].updateDebtIndex();
        // Get the possibly rebalanced amount of destroyed tokens
        uint256 destroyed = IKreskoAssetIssuer(_anchor).destroy(_burnAmount, msg.sender);
        // Calculate the debt index scaled amount
        uint256 amountScaled = destroyed.wadToRay().rayDiv(newDebtIndex);
        require(amountScaled != 0, Error.INVALID_SCALED_AMOUNT);

        // Decrease the principal debt
        self.kreskoAssetDebt[_account][_kreskoAsset] -= destroyed;
        // Decrease the scaled debt and set user asset's last debt index
        irs().srUserInfo[_account][_kreskoAsset].debtScaled -= uint128(amountScaled);
        irs().srUserInfo[_account][_kreskoAsset].lastDebtIndex = uint128(newDebtIndex);
        // Update the stability rate for the asset
        irs().srAssets[_kreskoAsset].updateStabilityRate();
    }

    /**
     * @notice Repays accrued stability rate interest for a single asset
     * @param _account Account to repay interest for
     * @param _kreskoAsset Kresko asset to repay interest for
     * @return kissRepayAmount amount repaid
     */
    function repayFullStabilityRateInterest(
        MinterState storage self,
        address _account,
        address _kreskoAsset
    ) internal returns (uint256 kissRepayAmount) {
        // Update debt index for the asset
        uint256 newDebtIndex = irs().srAssets[_kreskoAsset].updateDebtIndex();
        // Get the accrued interest in repayment token
        (, kissRepayAmount) = self.getKreskoAssetDebtInterest(_account, _kreskoAsset);

        // If no interest has accrued no further operations needed
        // Do not revert because we want the preserve new debt index and stability rate
        if (kissRepayAmount == 0) {
            // Update stability rate for asset
            irs().srAssets[_kreskoAsset].updateStabilityRate();
            return 0;
        }

        // Transfer the accrued interest
        IERC20Upgradeable(irs().kiss).safeTransferFrom(msg.sender, self.feeRecipient, kissRepayAmount);

        // Update scaled values for the user
        irs().srUserInfo[_account][_kreskoAsset].debtScaled = uint128(
            self.getKreskoAssetDebtPrincipal(_account, _kreskoAsset).wadToRay().rayDiv(newDebtIndex)
        );
        irs().srUserInfo[_account][_kreskoAsset].lastDebtIndex = uint128(newDebtIndex);

        // Remove from minted kresko assets if debt is cleared
        if (self.getKreskoAssetDebtPrincipal(_account, _kreskoAsset) == 0) {
            self.mintedKreskoAssets[_account].removeAddress(
                _kreskoAsset,
                self.getMintedKreskoAssetsIndex(_account, _kreskoAsset)
            );
        }

        // Update stability rates
        irs().srAssets[_kreskoAsset].updateStabilityRate();
        // Emit event with the account, asset and amount repaid
        emit InterestRateEvent.StabilityRateInterestRepaid(_account, _kreskoAsset, kissRepayAmount);
    }

    /**
     * @notice Charges the protocol close fee based off the value of the burned asset.
     * @dev Takes the fee from the account's collateral assets. Attempts collateral assets
     *   in reverse order of the account's deposited collateral assets array.
     * @param _account The account to charge the close fee from.
     * @param _kreskoAsset The address of the kresko asset being burned.
     * @param _burnAmount The amount of the kresko asset being burned.
     */
    function chargeCloseFee(
        MinterState storage self,
        address _account,
        address _kreskoAsset,
        uint256 _burnAmount
    ) internal {
        KrAsset memory krAsset = self.kreskoAssets[_kreskoAsset];
        // Calculate the value of the fee according to the value of the krAssets being burned.
        FixedPoint.Unsigned memory feeValue = krAsset.fixedPointUSD(_burnAmount).mul(krAsset.closeFee);

        // Do nothing if the fee value is 0.
        if (feeValue.rawValue == 0) {
            return;
        }

        address[] memory accountCollateralAssets = self.depositedCollateralAssets[_account];
        // Iterate backward through the account's deposited collateral assets to safely
        // traverse the array while still being able to remove elements if necessary.
        // This is because removing the last element of the array does not shift around
        // other elements in the array.

        for (uint256 i = accountCollateralAssets.length - 1; i >= 0; i--) {
            address collateralAssetAddress = accountCollateralAssets[i];

            (uint256 transferAmount, FixedPoint.Unsigned memory feeValuePaid) = self.calcFee(
                collateralAssetAddress,
                _account,
                feeValue,
                i
            );

            // Remove the transferAmount from the stored deposit for the account.
            self.collateralDeposits[_account][collateralAssetAddress] -= self
                .collateralAssets[collateralAssetAddress]
                .toNonRebasingAmount(transferAmount);

            // Transfer the fee to the feeRecipient.
            IERC20Upgradeable(collateralAssetAddress).safeTransfer(self.feeRecipient, transferAmount);
            emit MinterEvent.CloseFeePaid(_account, collateralAssetAddress, transferAmount, feeValuePaid.rawValue);

            feeValue = feeValue.sub(feeValuePaid);
            // If the entire fee has been paid, no more action needed.
            if (feeValue.rawValue == 0) {
                return;
            }
        }
    }

    /**
     * @notice Check that debt repaid does not leave a dust position, if it does:
     * return an amount that pays up to minDebtValue
     * @param _kreskoAsset The address of the kresko asset being burned.
     * @param _burnAmount The amount being burned
     * @param _debtAmount The debt amount of `_account`
     * @return amount == 0 or >= minDebtAmount
     */
    function ensureNotDustPosition(
        MinterState storage self,
        address _kreskoAsset,
        uint256 _burnAmount,
        uint256 _debtAmount
    ) internal view returns (uint256 amount) {
        // If the requested burn would put the user's debt position below the minimum
        // debt value, close up to the minimum debt value instead.
        FixedPoint.Unsigned memory krAssetValue = self.getKrAssetValue(_kreskoAsset, _debtAmount - _burnAmount, true);
        if (krAssetValue.isGreaterThan(0) && krAssetValue.isLessThan(self.minimumDebtValue)) {
            FixedPoint.Unsigned memory minDebtValue = self.minimumDebtValue.div(
                self.kreskoAssets[_kreskoAsset].fixedPointPrice()
            );
            amount = _debtAmount - minDebtValue.rawValue;
        } else {
            amount = _burnAmount;
        }
    }
}

src/contracts/minter/libs/LibStabilityRa.content:
// SPDX-License-Identifier: agpl-3.0
pragma solidity >=0.8.14;

import {IKreskoAsset} from "../../kreskoasset/IKreskoAsset.sol";
import {IERC20Upgradeable} from "../../shared/IERC20Upgradeable.sol";

import {FixedPoint} from "../../libs/FixedPoint.sol";
import {WadRay} from "../../libs/WadRay.sol";
import {Error} from "../../libs/Errors.sol";
import {Percentages} from "../../libs/Percentages.sol";
import {LibKrAsset} from "../libs/LibKrAsset.sol";

import {StabilityRateConfig} from "../InterestRateState.sol";
import {ms} from "../MinterStorage.sol";

/* solhint-disable not-rely-on-time */

/**
 * @author Kresko
 * @title AMM price stability rate library, derived from Aave Protocols VariableDebtToken calculations
 * @notice Library for performing stability rate related operations
 */
library LibStabilityRate {
    using WadRay for uint256;
    using WadRay for uint128;
    using Percentages for uint256;
    using FixedPoint for FixedPoint.Unsigned;

    /// @dev Ignoring leap years
    uint256 internal constant SECONDS_PER_YEAR = 365 days;

    /**
     * @notice Cumulates the stability rate from previous update and multiplies the debt index with it.
     * @dev Updates the updated timestamp
     * @dev New debt index cannot overflow uint128
     * @param self configuration for the asset
     * @return newDebtIndex the updated index
     */
    function updateDebtIndex(StabilityRateConfig storage self) internal returns (uint256 newDebtIndex) {
        if (self.asset == address(0)) return WadRay.RAY;

        newDebtIndex = self.debtIndex;
        // only cumulating if there is any assets minted and rate is over 0
        if (IERC20Upgradeable(self.asset).totalSupply() != 0) {
            uint256 cumulatedStabilityRate = self.calculateCompoundedInterest(block.timestamp);
            newDebtIndex = cumulatedStabilityRate.rayMul(self.debtIndex);
            require(newDebtIndex <= type(uint128).max, Error.DEBT_INDEX_OVERFLOW);
            self.debtIndex = uint128(newDebtIndex);
        }

        self.lastUpdateTimestamp = uint40(block.timestamp);
    }

    /**
     * @notice Updates the current stability rate for an asset
     * @dev New stability rate cannot overflow uint128
     * @param self rate configuration for the asset
     */
    function updateStabilityRate(StabilityRateConfig storage self) internal {
        if (self.asset == address(0)) return;

        uint256 stabilityRate = calculateStabilityRate(self);
        require(stabilityRate <= type(uint128).max, Error.STABILITY_RATE_OVERFLOW);
        self.stabilityRate = uint128(stabilityRate);
    }

    /**
     * @notice Get the current price rate between AMM and oracle pricing
     * @dev Raw return value of ammPrice == 0 when no AMM pair exists OR liquidity of the pair does not qualify
     * @param self rate configuration for the asset
     * @return priceRate the current price rate
     */
    function getPriceRate(StabilityRateConfig storage self) internal view returns (uint256 priceRate) {
        FixedPoint.Unsigned memory oraclePrice = ms().getKrAssetValue(self.asset, 1 ether, true);
        FixedPoint.Unsigned memory ammPrice = ms().getKrAssetAMMPrice(self.asset, 1 ether);
        // no pair, no effect
        if (ammPrice.rawValue == 0) {
            return 0;
        }
        return ammPrice.div(oraclePrice).div(10).rawValue;
    }

    /**
     * @notice Calculate new stability rate from the current price rate
     * @dev Separate calculations exist for following cases:
     * case 1: AMM premium < optimal
     * case 2: AMM premium > optimal
     * @param self rate configuration for the asset
     * @return stabilityRate the current stability rate
     */
    function calculateStabilityRate(StabilityRateConfig storage self) internal view returns (uint256 stabilityRate) {
        uint256 priceRate = self.getPriceRate(); // 0.95 RAY = -5% PREMIUM, 1.05 RAY = +5% PREMIUM
        // Return base rate if no AMM price exists
        if (priceRate == 0) {
            return self.stabilityRateBase;
        }
        bool rateIsGTOptimal = priceRate > self.optimalPriceRate;

        uint256 rateDiff = rateIsGTOptimal ? priceRate - self.optimalPriceRate : self.optimalPriceRate - priceRate;
        uint256 rateDiffAdjusted = rateDiff.rayMul(self.rateSlope2.rayDiv(self.rateSlope1 + self.priceRateDelta));

        if (!rateIsGTOptimal) {
            // Case: AMM price is lower than priceRate
            return self.stabilityRateBase + rateDiffAdjusted;
        } else {
            // Case: AMM price is higher than priceRate
            return self.stabilityRateBase.rayDiv(WadRay.RAY + rateDiffAdjusted);
        }
    }

    /**
     * @dev Function to calculate the interest using a compounded interest rate formula
     * To avoid expensive exponentiation, the calculation is performed using a binomial approximation:
     *
     *  (1+x)^n = 1+n*x+[n/2*(n-1)]*x^2+[n/6*(n-1)*(n-2)*x^3...
     *
     * The approximation slightly underpays liquidity providers and undercharges borrowers
     * with the advantage of great gas cost reductions
     * The Aave whitepaper contains reference to the approximation
     * with a table showing the margin of error per different time periods
     *
     * @param self rate configuration for the asset
     * @param _currentTimestamp The timestamp of the last update of the interest
     * @return The interest rate compounded during the timeDelta, in ray
     **/
    function calculateCompoundedInterest(
        StabilityRateConfig storage self,
        uint256 _currentTimestamp
    ) internal view returns (uint256) {
        //solium-disable-next-line
        uint256 exp = _currentTimestamp - uint256(self.lastUpdateTimestamp);

        if (exp == 0) {
            return WadRay.RAY;
        }

        uint256 expMinusOne;
        uint256 expMinusTwo;
        uint256 basePowerTwo;
        uint256 basePowerThree;
        unchecked {
            expMinusOne = exp - 1;

            expMinusTwo = exp > 2 ? exp - 2 : 0;

            basePowerTwo = self.stabilityRate.rayMul(self.stabilityRate) / (SECONDS_PER_YEAR * SECONDS_PER_YEAR);
            basePowerThree = basePowerTwo.rayMul(self.stabilityRate) / SECONDS_PER_YEAR;
        }

        uint256 secondTerm = exp * expMinusOne * basePowerTwo;
        unchecked {
            secondTerm /= 2;
        }
        uint256 thirdTerm = exp * expMinusOne * expMinusTwo * basePowerThree;
        unchecked {
            thirdTerm /= 6;
        }

        return WadRay.RAY + (self.stabilityRate * exp) / SECONDS_PER_YEAR + secondTerm + thirdTerm;
    }

    /**
     * @dev Returns the ongoing normalized debt index for the borrowers
     * A value of 1e27 means there is no interest. As time passes, the interest is accrued
     * A value of 2*1e27 means that for each unit of debt, one unit worth of interest has been accumulated
     * @param self rate configuration for the asset
     * @return The normalized debt index. expressed in ray
     **/
    function getNormalizedDebtIndex(StabilityRateConfig storage self) internal view returns (uint256) {
        if (self.asset == address(0)) return WadRay.RAY;
        //solium-disable-next-line
        if (self.lastUpdateTimestamp == uint40(block.timestamp)) {
            //if the index was updated in the same block, no need to perform any calculation
            return self.debtIndex;
        }

        return self.calculateCompoundedInterest(block.timestamp).rayMul(self.debtIndex);
    }
}

src/contracts/minter/MinterState.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.14;

import {LibKrAsset} from "./libs/LibKrAsset.sol";
import {LibAccount} from "./libs/LibAccount.sol";
import {LibCollateral} from "./libs/LibCollateral.sol";
import {LibCalculation} from "./libs/LibCalculation.sol";
import {LibRepay} from "./libs/LibRepay.sol";
import {LibMint} from "./libs/LibMint.sol";
import {FixedPoint} from "../libs/FixedPoint.sol";
import {Action, SafetyState, CollateralAsset, KrAsset} from "./MinterTypes.sol";

/* solhint-disable state-visibility */
using LibCalculation for MinterState global;
using LibKrAsset for MinterState global;
using LibCollateral for MinterState global;
using LibAccount for MinterState global;
using LibRepay for MinterState global;
using LibMint for MinterState global;

/**
 * @title Storage layout for the minter state
 * @author Kresko
 */
struct MinterState {
    /* -------------------------------------------------------------------------- */
    /*                               Initialization                               */
    /* -------------------------------------------------------------------------- */

    /// @notice Initialization version
    uint256 initializations;
    bytes32 domainSeparator;
    /* -------------------------------------------------------------------------- */
    /*                           Configurable Parameters                          */
    /* -------------------------------------------------------------------------- */

    /// @notice The recipient of protocol fees.
    address feeRecipient;
    /// @notice The factor used to calculate the incentive a liquidator receives in the form of seized collateral.
    FixedPoint.Unsigned liquidationIncentiveMultiplier;
    /// @notice The absolute minimum ratio of collateral value to debt value used to calculate collateral requirements.
    FixedPoint.Unsigned minimumCollateralizationRatio;
    /// @notice The minimum USD value of an individual synthetic asset debt position.
    FixedPoint.Unsigned minimumDebtValue;
    /// @notice The collateralization ratio at which positions may be liquidated.
    FixedPoint.Unsigned liquidationThreshold;
    /// @notice Flag tells if there is a need to perform safety checks on user actions
    bool safetyStateSet;
    /// @notice asset -> action -> state
    mapping(address => mapping(Action => SafetyState)) safetyState;
    /* -------------------------------------------------------------------------- */
    /*                              Collateral Assets                             */
    /* -------------------------------------------------------------------------- */

    /// @notice Mapping of collateral asset token address to information on the collateral asset.
    mapping(address => CollateralAsset) collateralAssets;
    /**
     * @notice Mapping of account -> asset -> deposit amount
     */
    mapping(address => mapping(address => uint256)) collateralDeposits;
    /// @notice Mapping of account -> collateral asset addresses deposited
    mapping(address => address[]) depositedCollateralAssets;
    /* -------------------------------------------------------------------------- */
    /*                                Kresko Assets                               */
    /* -------------------------------------------------------------------------- */

    /// @notice Mapping of kresko asset token address to information on the Kresko asset.
    mapping(address => KrAsset) kreskoAssets;
    /// @notice Mapping of account -> krAsset -> debt amount owed to the protocol
    mapping(address => mapping(address => uint256)) kreskoAssetDebt;
    /// @notice Mapping of account -> addresses of borrowed krAssets
    mapping(address => address[]) mintedKreskoAssets;
    /// @notice The AMM oracle address.
    address ammOracle;
    /// @notice Offchain oracle decimals
    uint8 extOracleDecimals;
}

